mapper.update:
UPDATE board
SET board_content = #{board_content}
WHERE board_no = #{board_no}

choose.when:
INSERT INTO board(board_content, user_id, target)
VALUES(
#{board_content},
#{user_id},
#{target}
)

choose.otherwise:
INSERT INTO board(board_title, board_content, user_id)
VALUES(
#{board_title},
#{board_content},
#{user_id}
)
